unit.select:
select      
  tc_kalan_gastos_clasificaciones.id_gasto_clasificacion as id_key,        
  if(tc_kalan_gastos_clasificaciones.id_activo= 1, 'SI', 'NO') as activo,
  tc_kalan_gastos_clasificaciones.*
from      
  tc_kalan_gastos_clasificaciones
where 
  id_activo= 1
  and {condicion} 
{sortOrder}
